import React, { useState, useEffect, useContext, useRef } from 'react';
import {
  Container,
  Typography,
  Paper,
  Button,
  Box,
  TextField,
  Grid,
  Card,
  CardContent,
  CardActions,
  Divider,
  CircularProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  IconButton,
  Alert,
  Snackbar,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  OutlinedInput,
  Chip,
  ListItemText,
  Checkbox
} from '@mui/material';
import { AuthContext } from '../context/AuthContext';
import { QRCodeCanvas } from 'qrcode.react';
import { Add, ContentCopy, Download, Delete, Edit, Restaurant, QrCode2 } from '@mui/icons-material';

const Menus = () => {
  const { user, authAxios } = useContext(AuthContext);
  const [menus, setMenus] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [openDialog, setOpenDialog] = useState(false);
  const [editingMenu, setEditingMenu] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    categories: []
  });
  const [snackbar, setSnackbar] = useState({
    open: false,
    message: '',
    severity: 'success'
  });
  
  const baseUrl = window.location.origin;
  const qrCodeRefs = useRef({});

  useEffect(() => {
    fetchCategories();
    fetchMenus();
  }, []);

  const fetchCategories = async () => {
    try {
      const response = await authAxios.get('/categories');
      setCategories(response.data.categories);
    } catch (error) {
      console.error('Kategoriler alınırken hata:', error);
      setSnackbar({
        open: true,
        message: 'Kategoriler alınırken hata oluştu',
        severity: 'error'
      });
    }
  };

  const fetchMenus = async () => {
    try {
      setLoading(true);
      const response = await authAxios.get('/menu/menus');
      setMenus(response.data.menus);
    } catch (error) {
      console.error('Menüler alınırken hata:', error);
      setSnackbar({
        open: true,
        message: 'Menüler alınırken hata oluştu',
        severity: 'error'
      });
    } finally {
      setLoading(false);
    }
  };

  const handleOpenDialog = (menu = null) => {
    if (menu) {
      setEditingMenu(menu);
      setFormData({
        name: menu.name,
        description: menu.description || '',
        categories: menu.categories.map(cat => 
          typeof cat === 'string' ? cat : cat._id
        )
      });
    } else {
      setEditingMenu(null);
      setFormData({
        name: `${user?.restaurantName || 'Restoran'} Menüsü`,
        description: 'QR kod ile erişilebilen dijital menü',
        categories: categories.length > 0 ? [categories[0]._id] : []
      });
    }
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
    setEditingMenu(null);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (formData.categories.length === 0) {
      setSnackbar({
        open: true,
        message: 'En az bir kategori seçmelisiniz',
        severity: 'error'
      });
      return;
    }
    
    try {
      if (editingMenu) {
        // Menü güncelleme
        await authAxios.put(`/menu/menus/${editingMenu._id}`, formData);
        setSnackbar({
          open: true,
          message: 'Menü başarıyla güncellendi',
          severity: 'success'
        });
      } else {
        // Yeni menü oluşturma
        await authAxios.post('/menu/menus', formData);
        setSnackbar({
          open: true,
          message: 'Menü başarıyla oluşturuldu',
          severity: 'success'
        });
      }
      handleCloseDialog();
      fetchMenus();
    } catch (error) {
      console.error('Menü işlemi sırasında hata:', error);
      setSnackbar({
        open: true,
        message: `Menü ${editingMenu ? 'güncellenirken' : 'oluşturulurken'} hata oluştu: ${error.response?.data?.message || error.message}`,
        severity: 'error'
      });
    }
  };

  const handleDeleteMenu = async (id) => {
    if (window.confirm('Bu menüyü ve ilişkili QR kodu silmek istediğinizden emin misiniz?')) {
      try {
        await authAxios.delete(`/menu/menus/${id}`);
        setSnackbar({
          open: true,
          message: 'Menü başarıyla silindi',
          severity: 'success'
        });
        fetchMenus();
      } catch (error) {
        console.error('Menü silinirken hata:', error);
        setSnackbar({
          open: true,
          message: 'Menü silinirken hata oluştu',
          severity: 'error'
        });
      }
    }
  };

  const handleCopyURL = (menu) => {
    const url = `${baseUrl}${menu.qrCode.url}`;
    navigator.clipboard.writeText(url)
      .then(() => {
        setSnackbar({
          open: true,
          message: 'Menü URL adresi kopyalandı',
          severity: 'success'
        });
      })
      .catch((err) => {
        console.error('URL kopyalanırken hata:', err);
        setSnackbar({
          open: true,
          message: 'URL kopyalanırken bir hata oluştu',
          severity: 'error'
        });
      });
  };

  const handleDownloadQR = (menu) => {
    const qrCanvas = qrCodeRefs.current[menu._id];
    if (!qrCanvas) return;

    const canvas = qrCanvas;
    const url = canvas.toDataURL('image/png');
    const link = document.createElement('a');
    link.download = `${menu.name.replace(/\s+/g, '-')}-qr.png`;
    link.href = url;
    link.click();
  };

  const handleCloseSnackbar = () => {
    setSnackbar((prev) => ({
      ...prev,
      open: false
    }));
  };

  // Bir kategorinin adını ID'sine göre bul
  const getCategoryName = (categoryId) => {
    const category = categories.find(cat => cat._id === categoryId);
    return category ? category.name : 'Bilinmeyen Kategori';
  };

  return (
    <Container maxWidth="lg">
      <Paper sx={{ p: 3, mb: 4 }}>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
          <Typography variant="h4" component="h1">
            Menüler
          </Typography>
          <Button 
            variant="contained" 
            startIcon={<Add />}
            onClick={() => handleOpenDialog()}
            disabled={categories.length === 0}
          >
            Yeni Menü Oluştur
          </Button>
        </Box>
        
        <Typography variant="body1" sx={{ mb: 3 }}>
          Menülerinizi buradan yönetebilirsiniz. Her menü için otomatik olarak QR kod oluşturulur.
          Müşterileriniz QR kodu okutarak menünüze kolayca erişebilirler.
        </Typography>

        {categories.length === 0 && (
          <Alert severity="warning" sx={{ mb: 3 }}>
            Menü oluşturabilmek için önce en az bir kategori eklemelisiniz.
            <Button 
              color="inherit" 
              size="small" 
              component="a" 
              href="/categories"
              sx={{ ml: 2 }}
            >
              Kategorilere Git
            </Button>
          </Alert>
        )}

        {loading ? (
          <Box sx={{ display: 'flex', justifyContent: 'center', my: 4 }}>
            <CircularProgress />
          </Box>
        ) : menus.length === 0 ? (
          <Alert severity="info">
            Henüz menü oluşturmadınız. Yeni bir menü eklemek için "Yeni Menü Oluştur" butonuna tıklayın.
          </Alert>
        ) : (
          <Grid container spacing={3}>
            {menus.map((menu) => (
              <Grid item xs={12} sm={6} md={4} key={menu._id}>
                <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                  <CardContent sx={{ flexGrow: 1 }}>
                    <Typography variant="h5" component="div" gutterBottom>
                      {menu.name}
                    </Typography>
                    <Typography variant="body2" color="text.secondary" paragraph>
                      {menu.description || 'Açıklama yok'}
                    </Typography>
                    
                    <Box sx={{ mt: 2, mb: 2 }}>
                      <Typography variant="subtitle2" gutterBottom>
                        Kategoriler:
                      </Typography>
                      <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
                        {menu.categories.map((cat) => (
                          <Chip 
                            key={typeof cat === 'string' ? cat : cat._id}
                            label={typeof cat === 'string' ? getCategoryName(cat) : cat.name} 
                            size="small" 
                          />
                        ))}
                      </Box>
                    </Box>
                    
                    <Box sx={{ my: 2, display: 'flex', justifyContent: 'center' }}>
                      <QRCodeCanvas
                        id={`qr-code-${menu._id}`}
                        value={`${baseUrl}${menu.qrCode.url}`}
                        size={150}
                        level="H"
                        includeMargin
                        ref={(ref) => {
                          if (ref) {
                            qrCodeRefs.current[menu._id] = ref;
                          }
                        }}
                      />
                    </Box>
                    
                    <Typography variant="body2" color="text.secondary" align="center" sx={{ mt: 1 }}>
                      Görüntülenme: {menu.qrCode.views || 0}
                    </Typography>
                  </CardContent>
                  <Divider />
                  <CardActions>
                    <Button 
                      size="small" 
                      startIcon={<Edit />}
                      onClick={() => handleOpenDialog(menu)}
                    >
                      Düzenle
                    </Button>
                    <Button 
                      size="small" 
                      startIcon={<ContentCopy />}
                      onClick={() => handleCopyURL(menu)}
                    >
                      URL
                    </Button>
                    <Button 
                      size="small" 
                      startIcon={<Download />}
                      onClick={() => handleDownloadQR(menu)}
                    >
                      QR
                    </Button>
                    <Button 
                      size="small" 
                      color="error"
                      startIcon={<Delete />}
                      onClick={() => handleDeleteMenu(menu._id)}
                    >
                      Sil
                    </Button>
                  </CardActions>
                </Card>
              </Grid>
            ))}
          </Grid>
        )}
      </Paper>

      {/* Menü Ekleme/Düzenleme Dialog */}
      <Dialog open={openDialog} onClose={handleCloseDialog} fullWidth maxWidth="md">
        <DialogTitle>
          {editingMenu ? 'Menü Düzenle' : 'Yeni Menü Oluştur'}
        </DialogTitle>
        <form onSubmit={handleSubmit}>
          <DialogContent>
            <TextField
              autoFocus
              margin="dense"
              name="name"
              label="Menü Adı"
              type="text"
              fullWidth
              required
              value={formData.name}
              onChange={handleInputChange}
            />
            <TextField
              margin="dense"
              name="description"
              label="Menü Açıklaması"
              type="text"
              fullWidth
              multiline
              rows={3}
              value={formData.description}
              onChange={handleInputChange}
            />
            
            <FormControl fullWidth margin="dense">
              <InputLabel id="categories-label">Kategoriler</InputLabel>
              <Select
                labelId="categories-label"
                name="categories"
                multiple
                value={formData.categories}
                onChange={handleInputChange}
                input={<OutlinedInput label="Kategoriler" />}
                renderValue={(selected) => (
                  <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
                    {selected.map((value) => (
                      <Chip key={value} label={getCategoryName(value)} />
                    ))}
                  </Box>
                )}
                required
              >
                {categories.map((category) => (
                  <MenuItem key={category._id} value={category._id}>
                    <Checkbox checked={formData.categories.indexOf(category._id) > -1} />
                    <ListItemText primary={category.name} />
                  </MenuItem>
                ))}
              </Select>
              <FormControl.HelperText>
                Bu kategorilerdeki tüm ürünler menüde görüntülenecektir.
              </FormControl.HelperText>
            </FormControl>
            
            <Alert severity="info" sx={{ mt: 2 }}>
              Menü oluşturduğunuzda otomatik olarak bir QR kod oluşturulacaktır.
              Müşterileriniz bu QR kodu okutarak menünüzü görüntüleyebilirler.
            </Alert>
          </DialogContent>
          <DialogActions>
            <Button onClick={handleCloseDialog}>İptal</Button>
            <Button type="submit" variant="contained">
              {editingMenu ? 'Güncelle' : 'Oluştur'}
            </Button>
          </DialogActions>
        </form>
      </Dialog>

      {/* Bildirim Snackbar */}
      <Snackbar 
        open={snackbar.open} 
        autoHideDuration={6000} 
        onClose={handleCloseSnackbar}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
      >
        <Alert onClose={handleCloseSnackbar} severity={snackbar.severity}>
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Container>
  );
};

export default Menus;
