import React, { useState, useEffect, useContext, useRef } from 'react';
import {
  Container,
  Typography,
  Paper,
  Button,
  Box,
  TextField,
  Grid,
  Card,
  CardContent,
  CardActions,
  Divider,
  CircularProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  IconButton,
  Alert,
  Snackbar
} from '@mui/material';
import { AuthContext } from '../context/AuthContext';
import { QRCodeCanvas } from 'qrcode.react';
import { Add, ContentCopy, Download, Delete } from '@mui/icons-material';

const QRCodes = () => {
  const { user, authAxios } = useContext(AuthContext);
  const [qrCodes, setQRCodes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [openDialog, setOpenDialog] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    description: ''
  });
  const [snackbar, setSnackbar] = useState({
    open: false,
    message: '',
    severity: 'success'
  });
  
  const baseUrl = window.location.origin;
  const qrCodeRefs = useRef({});

  useEffect(() => {
    fetchQRCodes();
  }, []);

  const fetchQRCodes = async () => {
    try {
      setLoading(true);
      const response = await authAxios.get('/qr');
      setQRCodes(response.data.qrCodes);
    } catch (error) {
      console.error('QR kodları alınırken hata:', error);
      setSnackbar({
        open: true,
        message: 'QR kodları alınırken hata oluştu',
        severity: 'error'
      });
    } finally {
      setLoading(false);
    }
  };

  const handleOpenDialog = () => {
    setFormData({
      name: `${user?.restaurantName || 'Restoran'} Menü`,
      description: 'QR kod ile erişilebilen dijital menü'
    });
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await authAxios.post('/qr', formData);
      setSnackbar({
        open: true,
        message: 'QR kod başarıyla oluşturuldu',
        severity: 'success'
      });
      handleCloseDialog();
      fetchQRCodes();
    } catch (error) {
      console.error('QR kod oluşturulurken hata:', error);
      setSnackbar({
        open: true,
        message: 'QR kod oluşturulurken bir hata oluştu',
        severity: 'error'
      });
    }
  };

  const handleDeleteQR = async (id) => {
    if (window.confirm('Bu QR kodu silmek istediğinizden emin misiniz?')) {
      try {
        await authAxios.delete(`/qr/${id}`);
        setSnackbar({
          open: true,
          message: 'QR kod başarıyla silindi',
          severity: 'success'
        });
        fetchQRCodes();
      } catch (error) {
        console.error('QR kod silinirken hata:', error);
        setSnackbar({
          open: true,
          message: 'QR kod silinirken bir hata oluştu',
          severity: 'error'
        });
      }
    }
  };

  const handleCopyURL = (url) => {
    const fullUrl = `${baseUrl}${url}`;
    navigator.clipboard.writeText(fullUrl)
      .then(() => {
        setSnackbar({
          open: true,
          message: 'Menü URL adresi kopyalandı',
          severity: 'success'
        });
      })
      .catch((err) => {
        console.error('URL kopyalanırken hata:', err);
        setSnackbar({
          open: true,
          message: 'URL kopyalanırken bir hata oluştu',
          severity: 'error'
        });
      });
  };

  const handleDownloadQR = (id) => {
    const qrCanvas = qrCodeRefs.current[id];
    if (!qrCanvas) return;

    const qrCode = qrCodes.find(qr => qr._id === id);
    if (!qrCode) return;

    const canvas = qrCanvas.querySelector('canvas');
    if (!canvas) return;
    
    const url = canvas.toDataURL('image/png');
    const link = document.createElement('a');
    link.download = `${qrCode.name.replace(/\s+/g, '-')}-qr.png`;
    link.href = url;
    link.click();
  };

  const handleCloseSnackbar = () => {
    setSnackbar((prev) => ({
      ...prev,
      open: false
    }));
  };

  return (
    <Container maxWidth="lg">
      <Paper sx={{ p: 3, mb: 4 }}>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
          <Typography variant="h4" component="h1">
            QR Kodları
          </Typography>
          <Button 
            variant="contained" 
            startIcon={<Add />}
            onClick={handleOpenDialog}
          >
            Yeni QR Kod Oluştur
          </Button>
        </Box>
        <Typography variant="body1" sx={{ mb: 3 }}>
          QR kodlarınızı buradan yönetebilirsiniz. Oluşturduğunuz QR kodları müşterilerinizle paylaşarak
          menünüze hızlı erişim sağlayabilirsiniz.
        </Typography>

        {loading ? (
          <Box sx={{ display: 'flex', justifyContent: 'center', my: 4 }}>
            <CircularProgress />
          </Box>
        ) : qrCodes.length === 0 ? (
          <Alert severity="info">
            Henüz QR kod oluşturmadınız. Menünüz için QR kod oluşturmak için "Yeni QR Kod Oluştur" butonuna tıklayın.
          </Alert>
        ) : (
          <Grid container spacing={3}>
            {qrCodes.map((qrCode) => (
              <Grid item xs={12} sm={6} md={4} key={qrCode._id}>
                <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                  <CardContent sx={{ flexGrow: 1, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                    <Typography variant="h6" component="div" gutterBottom>
                      {qrCode.name}
                    </Typography>
                    
                    <Box 
                      sx={{ my: 2, p: 2, bgcolor: '#fff', borderRadius: 1 }}
                      ref={(el) => qrCodeRefs.current[qrCode._id] = el}
                    >
                      <QRCodeCanvas
                        id={`qr-code-${qrCode._id}`}
                        value={`${baseUrl}${qrCode.url}`}
                        size={180}
                        bgColor={"#ffffff"}
                        fgColor={"#000000"}
                        level={"H"}
                        includeMargin={true}
                      />
                    </Box>
                    
                    <Typography variant="body2" color="text.secondary" paragraph>
                      {qrCode.description || 'Açıklama yok'}
                    </Typography>
                    
                    <Typography variant="body2" color="text.secondary" sx={{ wordBreak: 'break-all' }}>
                      {baseUrl}{qrCode.url}
                    </Typography>
                    
                    <Typography variant="subtitle2" color="primary" sx={{ mt: 1 }}>
                      Görüntülenme: {qrCode.views || 0}
                    </Typography>
                  </CardContent>
                  <Divider />
                  <CardActions>
                    <Button 
                      size="small" 
                      startIcon={<ContentCopy />}
                      onClick={() => handleCopyURL(qrCode.url)}
                    >
                      URL Kopyala
                    </Button>
                    <Button 
                      size="small" 
                      startIcon={<Download />}
                      onClick={() => handleDownloadQR(qrCode._id)}
                    >
                      İndir
                    </Button>
                    <Button 
                      size="small" 
                      color="error"
                      startIcon={<Delete />}
                      onClick={() => handleDeleteQR(qrCode._id)}
                    >
                      Sil
                    </Button>
                  </CardActions>
                </Card>
              </Grid>
            ))}
          </Grid>
        )}
      </Paper>

      {/* QR Kod Oluşturma Dialog */}
      <Dialog open={openDialog} onClose={handleCloseDialog} maxWidth="sm" fullWidth>
        <DialogTitle>
          Yeni QR Kod Oluştur
        </DialogTitle>
        <form onSubmit={handleSubmit}>
          <DialogContent>
            <TextField
              autoFocus
              margin="dense"
              name="name"
              label="QR Kod Adı"
              type="text"
              fullWidth
              required
              value={formData.name}
              onChange={handleInputChange}
            />
            <TextField
              margin="dense"
              name="description"
              label="Açıklama"
              type="text"
              fullWidth
              multiline
              rows={3}
              value={formData.description}
              onChange={handleInputChange}
            />
            
            <Alert severity="info" sx={{ mt: 2 }}>
              Oluşturulan QR kod, tüm menü öğelerinizi içeren bir sayfaya yönlendirecektir.
              Müşterileriniz tarayıcılarında açarak menünüzü görüntüleyebilirler.
            </Alert>
          </DialogContent>
          <DialogActions>
            <Button onClick={handleCloseDialog}>İptal</Button>
            <Button type="submit" variant="contained">
              QR Kod Oluştur
            </Button>
          </DialogActions>
        </form>
      </Dialog>

      {/* Bildirim Snackbar */}
      <Snackbar 
        open={snackbar.open} 
        autoHideDuration={6000} 
        onClose={handleCloseSnackbar}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
      >
        <Alert onClose={handleCloseSnackbar} severity={snackbar.severity}>
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Container>
  );
};

export default QRCodes;
