import React, { useContext } from 'react';
import { AppBar, Toolbar, Typography, Button, Box, Container } from '@mui/material';
import { Link as RouterLink, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';

const Header = () => {
  const { isAuthenticated, logout, user } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <AppBar position="static">
      <Container>
        <Toolbar>
          <Typography
            variant="h6"
            component={RouterLink}
            to="/"
            sx={{
              textDecoration: 'none',
              color: 'white',
              flexGrow: 1,
              fontWeight: 'bold'
            }}
          >
            QR Menü Sistemi
          </Typography>

          {isAuthenticated ? (
            <>
              <Box sx={{ display: { xs: 'none', md: 'flex' } }}>
                <Button
                  component={RouterLink}
                  to="/"
                  color="inherit"
                >
                  Dashboard
                </Button>
                <Button
                  component={RouterLink}
                  to="/categories"
                  color="inherit"
                >
                  Kategoriler
                </Button>
                <Button
                  component={RouterLink}
                  to="/menu-items"
                  color="inherit"
                >
                  Menü Öğeleri
                </Button>
                <Button
                  component={RouterLink}
                  to="/qr-codes"
                  color="inherit"
                >
                  QR Kodlar
                </Button>
              </Box>
              <Box sx={{ display: 'flex', alignItems: 'center', ml: 2 }}>
                <Typography variant="body2" sx={{ mr: 1 }}>
                  {user?.name || 'Kullanıcı'}
                </Typography>
                <Button
                  color="inherit"
                  onClick={handleLogout}
                  variant="outlined"
                  size="small"
                  sx={{ borderColor: 'white' }}
                >
                  Çıkış
                </Button>
              </Box>
            </>
          ) : (
            <Box>
              <Button
                component={RouterLink}
                to="/login"
                color="inherit"
                sx={{ mr: 1 }}
              >
                Giriş
              </Button>
              <Button
                component={RouterLink}
                to="/register"
                color="inherit"
                variant="outlined"
                sx={{ borderColor: 'white' }}
              >
                Kayıt Ol
              </Button>
            </Box>
          )}
        </Toolbar>
      </Container>
    </AppBar>
  );
};

export default Header;
