import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import {
  Container,
  Typography,
  Paper,
  Box,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Divider,
  Chip,
  Stack,
  CircularProgress,
  Alert,
  List,
  ListItem,
  ListItemText,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  Tab,
  Tabs
} from '@mui/material';
import {
  ExpandMore,
  Restaurant,
  RestaurantMenu,
  Info,
  Star,
  LocalOffer
} from '@mui/icons-material';

const PublicMenu = () => {
  const { qrId } = useParams();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [restaurant, setRestaurant] = useState(null);
  const [categories, setCategories] = useState([]);
  const [menuItems, setMenuItems] = useState([]);
  const [activeCategory, setActiveCategory] = useState(0);

  useEffect(() => {
    const fetchMenu = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`/api/qr/menu/${qrId}`);
        setRestaurant(response.data.restaurant);
        setCategories(response.data.categories);
        setMenuItems(response.data.menuItems);
      } catch (error) {
        console.error('Menü bilgileri alınırken hata:', error);
        setError('Menü bilgileri alınamadı. QR kod geçerli değil veya menü bulunamadı.');
      } finally {
        setLoading(false);
      }
    };

    if (qrId) {
      fetchMenu();
    }
  }, [qrId]);

  const handleCategoryChange = (event, newValue) => {
    setActiveCategory(newValue);
  };

  // Menü öğelerini kategoriye göre filtrele
  const filteredItems = activeCategory === 0 
    ? menuItems // Tüm ürünler
    : menuItems.filter(item => 
        item.category === categories[activeCategory - 1]._id || 
        (item.category._id && item.category._id === categories[activeCategory - 1]._id)
      );

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      {loading ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', my: 8 }}>
          <CircularProgress />
        </Box>
      ) : error ? (
        <Alert severity="error" sx={{ my: 4 }}>
          {error}
        </Alert>
      ) : (
        <>
          <Paper sx={{ p: 3, mb: 4, background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)' }}>
            <Typography variant="h3" component="h1" gutterBottom align="center" sx={{ color: 'white' }}>
              {restaurant?.restaurantName || 'Restoran Menüsü'}
            </Typography>
            
            <Typography variant="subtitle1" align="center" sx={{ mb: 2, color: 'white' }}>
              QR Menü Sistemi
            </Typography>
          </Paper>

          {/* Kategori Sekmeler */}
          <Paper sx={{ mb: 4 }}>
            <Tabs
              value={activeCategory}
              onChange={handleCategoryChange}
              variant="scrollable"
              scrollButtons="auto"
              sx={{ borderBottom: 1, borderColor: 'divider' }}
            >
              <Tab label="Tümü" icon={<RestaurantMenu />} iconPosition="start" />
              {categories.map((category) => (
                <Tab key={category._id} label={category.name} />
              ))}
            </Tabs>
          </Paper>

          {/* Menü Öğeleri */}
          {filteredItems.length === 0 ? (
            <Alert severity="info">
              Bu kategoride ürün bulunmamaktadır.
            </Alert>
          ) : (
            <Grid container spacing={3}>
              {filteredItems.map((item) => (
                <Grid item xs={12} sm={6} md={4} key={item._id}>
                  <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                    <CardMedia
                      component="img"
                      height="160"
                      image={item.image || 'https://via.placeholder.com/300x160?text=Ürün+Görseli'}
                      alt={item.name}
                    />
                    <CardContent sx={{ flexGrow: 1 }}>
                      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
                        <Typography variant="h6" component="div" gutterBottom>
                          {item.name}
                          {item.popular && (
                            <Chip 
                              label="Popüler" 
                              color="secondary" 
                              size="small" 
                              icon={<Star />}
                              sx={{ ml: 1 }} 
                            />
                          )}
                        </Typography>
                        <Typography variant="h6" color="primary" sx={{ fontWeight: 'bold' }}>
                          {item.price.toFixed(2)} ₺
                        </Typography>
                      </Box>
                      
                      <Typography variant="body2" color="text.secondary" paragraph>
                        {item.description || 'Açıklama yok'}
                      </Typography>
                      
                      {!item.isAvailable && (
                        <Chip 
                          label="Stokta Yok" 
                          color="default" 
                          size="small" 
                          sx={{ mb: 1 }} 
                        />
                      )}
                      
                      {item.ingredients && item.ingredients.length > 0 && (
                        <Box sx={{ mt: 1 }}>
                          <Typography variant="body2" color="text.secondary">
                            <strong>İçindekiler:</strong>
                          </Typography>
                          <Stack direction="row" spacing={1} sx={{ mt: 0.5, flexWrap: 'wrap', gap: 0.5 }}>
                            {item.ingredients.map((ing, idx) => (
                              <Chip key={idx} label={ing} size="small" variant="outlined" />
                            ))}
                          </Stack>
                        </Box>
                      )}
                      
                      {item.allergens && item.allergens.length > 0 && (
                        <Box sx={{ mt: 1 }}>
                          <Typography variant="body2" color="text.secondary">
                            <strong>Alerjenler:</strong>
                          </Typography>
                          <Stack direction="row" spacing={1} sx={{ mt: 0.5, flexWrap: 'wrap', gap: 0.5 }}>
                            {item.allergens.map((alg, idx) => (
                              <Chip key={idx} label={alg} size="small" color="error" variant="outlined" />
                            ))}
                          </Stack>
                        </Box>
                      )}
                    </CardContent>
                  </Card>
                </Grid>
              ))}
            </Grid>
          )}
          
          {/* Restoran Bilgileri */}
          <Paper sx={{ p: 3, mt: 4 }}>
            <Accordion>
              <AccordionSummary
                expandIcon={<ExpandMore />}
                aria-controls="restaurant-info-content"
                id="restaurant-info-header"
              >
                <Typography sx={{ display: 'flex', alignItems: 'center' }}>
                  <Info sx={{ mr: 1 }} />
                  Restoran Bilgileri
                </Typography>
              </AccordionSummary>
              <AccordionDetails>
                <Box>
                  <Typography variant="h6" gutterBottom>
                    {restaurant?.restaurantName}
                  </Typography>
                  <Typography variant="body2" paragraph>
                    Bu menü QR kod sistemi kullanılarak oluşturulmuştur.
                  </Typography>
                </Box>
              </AccordionDetails>
            </Accordion>
          </Paper>
        </>
      )}
    </Container>
  );
};

export default PublicMenu;
